class Solution:
    def longestPalindrome(self, s):
        dict = {}
        for i in s:
            if i not in dict:
                dict[i] = 1
            else:
                dict[i] += 1
        
        res = 0
        add = 0
        for j in dict.values():
            if j%2 == 0:
                res += j
            else:
                res += j - 1
                add = 1
        
        return res+add
        
s = ""
ans = Solution().longestPalindrome(s)